import React from 'react';
import { makeStyles } from '@mui/styles';
import { Typography, Box, Button } from '@mui/material';
import { Link } from 'react-router-dom';
import honeyImage from '../images/honey-comb-pattern.png';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundImage: `url(${honeyImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    padding: theme.spacing(3),
    borderTop: `10px solid ${theme.palette.secondary.main}`,
  },
  basketIcon: {
    fontSize: '4rem',
    marginBottom: theme.spacing(2),
  },
  item: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    margin: theme.spacing(1, 0),
    padding: theme.spacing(2),
    border: `1px solid ${theme.palette.secondary.main}`,
    borderRadius: theme.spacing(1),
  },
}));

const Basket = ({ cartItems, removeFromCart }) => {
  const classes = useStyles();

  return (
    <Box className={classes.root}>
      <Typography variant="h4">
        <BasketIcon className={classes.basketIcon} />
        Kosár
      </Typography>
      {cartItems.length === 0 ? (
        <Typography variant="body1">A kosár üres.</Typography>
      ) : (
        cartItems.map((item) => (
          <Box className={classes.item} key={item.id}>
            <img className={classes.image} src={honeyImage} alt={item.name} />
            <Typography variant="h6">{item.name}</Typography>
            <Typography variant="body1">{item.description}</Typography>
            <Typography variant="h5">Ár: {item.price} HUF</Typography>
            <Button variant="contained" color="warning" onClick={() => removeFromCart(item)}>
              Törlés
            </Button>
          </Box>
        ))
      )}
      <Link to="/list">
        <Button variant="contained" color="primary">
          Vissza a vásárláshoz
        </Button>
      </Link>
    </Box>
  );
};

export default Basket;
