const honeyTypes = ['akác', 'gyógy', 'hárs', 'virág', 'repce'];

import React from 'react';
import { makeStyles } from '@mui/styles';
import { Grid, Typography, Card, CardActionArea, CardMedia, CardContent } from '@mui/material';

const products = [
  {
    id: 1,
    name: 'Product 1',
    image: 'https://via.placeholder.com/300x200.png?text=Product+1',
    price: 19.99,
  },
  {
    id: 2,
    name: 'Product 2',
    image: 'https://via.placeholder.com/300x200.png?text=Product+2',
    price: 29.99,
  },
  {
    id: 3,
    name: 'Product 3',
    image: 'https://via.placeholder.com/300x200.png?text=Product+3',
    price: 39.99,
  },
  {
    id: 4,
    name: 'Product 4',
    image: 'https://via.placeholder.com/300x200.png?text=Product+4',
    price: 49.99,
  },
];

const useStyles = makeStyles((theme) => ({
    root: {
      padding: theme.spacing(3),
    },
    title: {
      marginBottom: theme.spacing(2),
    },
    select: {
      marginRight: theme.spacing(2),
    },
  }));
  
  
  const ListPage = () => {
    const classes = useStyles();
  
    const [selectedType, setSelectedType] = useState(honeyTypes[0]);
  
    const handleTypeChange = (event) => {
      setSelectedType(event.target.value);
    };
  
    const filteredProducts = products.filter(
      (product) => product.category === selectedType
    );
  
    return (
      <div className={classes.root}>
        <Typography variant="h4" className={classes.title}>
          Honey Types
        </Typography>
        <Grid container justifyContent="flex-end" alignItems="center">
          <Typography variant="subtitle1" className={classes.select}>
            Select a honey type:
          </Typography>
          <Select value={selectedType} onChange={handleTypeChange}>
            {honeyTypes.map((type) => (
              <MenuItem key={type} value={type}>
                {type}
              </MenuItem>
            ))}
          </Select>
        </Grid>
        <Grid container spacing={3}>
          {filteredProducts.map((product) => (
            <Grid item key={product.id} xs={12} sm={6} md={4}>
              <ProductCard product={product} />
            </Grid>
          ))}
        </Grid>
      </div>
    );
  };

  export default List;
