import React, { useState } from 'react';
import { makeStyles } from '@mui/styles';
import { Typography, Grid, Box, Button } from '@mui/material';
import { Link } from 'react-router-dom';

const honeyTypes = [
    {
      id: 1,
      name: 'Akác',
      description: 'Leírás Akác mézről',
      price: 1000,
      image: 'https://via.placeholder.com/200'
    },
    {
      id: 2,
      name: 'Gyógy',
      description: 'Leírás Gyógy mézről',
      price: 1200,
      image: 'https://via.placeholder.com/200'
    },
    {
      id: 3,
      name: 'Hárs',
      description: 'Leírás Hárs mézről',
      price: 1500,
      image: 'https://via.placeholder.com/200'
    },
    {
      id: 4,
      name: 'Virág',
      description: 'Leírás Virág mézről',
      price: 800,
      image: 'https://via.placeholder.com/200'
    },
    {
      id: 5,
      name: 'Repce',
      description: 'Leírás Repce mézről',
      price: 900,
      image: 'https://via.placeholder.com/200'
    }
  ];

const useStyles = makeStyles(() => ({
  root: {
    backgroundColor: 'white',
    padding: '20px',
  },
  item: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    margin: '20px',
  },
  image: {
    height: '200px',
    width: '200px',
    marginBottom: '10px',
  },
}));

const List: React.FC = () => {
  const classes = useStyles();
  const [cartItems, setCartItems] = useState<string[]>([]);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
    console.log(cartItems)
  };

  return (
    <Grid container>
    {honeyTypes.map((type) => (
        <Grid item xs={12} sm={6} md={4} lg={3} key={type.id}>
            <Box className={classes.item}>
                <img className={classes.image} src={type.image} alt={type.name} />
                <Typography variant="h6">{type.name}</Typography>
                <Typography variant="body1">{type.description}</Typography>
                <Typography variant="h5">Ár: {type.price} HUF</Typography>
                <Button variant="contained" color="warning" onClick={() => addToCart(type)}>
                    Kosárba
                </Button>
            </Box>
        </Grid>
    ))}
    <Link to={{ pathname: '/basket', state: { cartItems: cartItems } }}>
    <button className="btn btn-primary">View Basket</button>
</Link>
    </Grid>
  );
};

export default List;
