import { Box, Button, Container, Grid, Typography } from '@mui/material';
import { makeStyles } from '@mui/styles';
import React from 'react';
import { Link } from 'react-router-dom';
import honeyImage  from '../images/honey-comb-pattern.png'

const useStyles = makeStyles(() => ({
    root: {
      backgroundColor: '#f2f2f2',
      padding: '20px',
      minHeight: '100vh',
    },
    item: {
      backgroundColor: '#fff',
      padding: '10px',
      borderRadius: '10px',
      boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)',
      transition: 'all 0.3s ease-in-out',
      '&:hover': {
        transform: 'scale(1.05)',
      },
    },
    image: {
      height: '100%',
      width: '100%',
      objectFit: 'cover',
      borderRadius: '10px',
      marginBottom: '10px',
    },
    button: {
      marginTop: '10px',
      backgroundColor: '#f9a826',
      color: '#fff',
      '&:hover': {
        backgroundColor: '#f48c06',
      },
    },
    name: {
      fontWeight: 'bold',
      marginBottom: '5px',
      fontSize: '40%'
    },
    price: {
      fontWeight: 'bold',
      fontSize: '1.2rem',
    },
  }));

  const items = [
    {
      id: 1,
      name: 'Akác',
      description: 'Leírás Akác mézről',
      price: 1000
    },
    {
      id: 2,
      name: 'Gyógy',
      description: 'Leírás Gyógy mézről',
      price: 1200
    },
    {
      id: 3,
      name: 'Hárs',
      description: 'Leírás Hárs mézről',
      price: 1500
    },
    {
      id: 4,
      name: 'Virág',
      description: 'Leírás Virág mézről',
      price: 800
    },
    {
      id: 5,
      name: 'Repce',
      description: 'Leírás Repce mézről',
      price: 900
    }
  ];


const List = ({ addToCart }) => {
    const classes = useStyles();

    return (
        <Container maxWidth="lg">
        <Box>
            <Typography variant="h4">Termékek</Typography>
            <Grid container>
            {items.map((type) => (
                <Grid item xs={12} sm={6} md={4} lg={3} >
                <Box className={classes.item}>
                    <img  className={classes.image} src={honeyImage}/>
                    <Typography className={classes.name}>{type.name}</Typography>
                    <Typography variant="body1">{type.description}</Typography>
                    <Typography className={classes.price}>Ár: {type.price} HUF</Typography>
                    <Button className={classes.button} variant="contained" color="warning" onClick={() => addToCart(type)}>Add to Cart</Button>
                </Box>
                </Grid>
            ))}
            </Grid>
        </Box>
        <Link to="/basket">
            <Button className={classes.button} variant="contained" color="primary">Tovább a kosárhoz</Button>
        </Link>
        </Container>
    );
};

export default List;
