import React from 'react';
import { makeStyles } from '@mui/styles';
import { Typography, Grid, Container, Box, Button } from '@mui/material';

const useStyles = makeStyles(() => ({
  root: {
    backgroundColor: 'white',
    padding: '20px',
  },
  item: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    margin: '20px',
  },
  image: {
    height: '200px',
    width: '200px',
    marginBottom: '10px',
  },
}));

const honeyTypes = [
    { 
      name: 'Akác',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum et gravida ex. Duis in luctus urna, vel fringilla velit. Aenean vestibulum eu justo eget congue.',
      price: 1000
    },
    {
      name: 'Gyógy',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum et gravida ex. Duis in luctus urna, vel fringilla velit. Aenean vestibulum eu justo eget congue.',
      price: 1500
    },
    {
      name: 'Hárs',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum et gravida ex. Duis in luctus urna, vel fringilla velit. Aenean vestibulum eu justo eget congue.',
      price: 1200
    },
    {
      name: 'Virág',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum et gravida ex. Duis in luctus urna, vel fringilla velit. Aenean vestibulum eu justo eget congue.',
      price: 800
    },
    {
      name: 'Repce',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vestibulum et gravida ex. Duis in luctus urna, vel fringilla velit. Aenean vestibulum eu justo eget congue.',
      price: 1100
    }
  ];

const Cart = ({ cartItems }) => {
  return (
    <Container maxWidth="lg">
      <Box>
        {cartItems.length === 0 ? (
          <Typography variant="h5">A kosár üres</Typography>
        ) : (
          <>
            <Typography variant="h4">Kosár</Typography>
            <Grid container>
              {cartItems.map((item) => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={item.id}>
                  <Box className={classes.item}>
                    <img className={classes.image} src="https://via.placeholder.com/200" alt={item.name} />
                    <Typography variant="h6">{item.name}</Typography>
                    <Typography variant="body1">Leírás</Typography>
                    <Typography variant="h5">Ár: {item.price} Ft</Typography>
                    <Button variant="contained" color="warning">
                      Kosárba
                    </Button>
                  </Box>
                </Grid>
              ))}
            </Grid>
          </>
        )}
      </Box>
    </Container>
  );
};

const List = () => {
  const classes = useStyles();
  const [cartItems, setCartItems] = React.useState([]);

  const handleAddToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  return (
    <>
      <Container maxWidth="lg">
        <Box className={classes.root}>
          <Typography variant="h4">Termékek</Typography>
          <Grid container>
            {honeyTypes.map((type) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={type}>
                <Box className={classes.item}>
                  <img className={classes.image} src="https://via.placeholder.com/200" alt={type} />
                  <Typography variant="h6">{type}</Typography>
                  <Typography variant="body1">Leírás</Typography>
                  <Typography variant="h5">Ár: 1000 Ft</Typography>
                  <Button variant="contained" color="warning" onClick={() => handleAddToCart({ id: type, name: type, price: 1000 })}>
                    Kosárba
                  </Button>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
      <Cart cartItems={cartItems} />
    </>
  );
};

export default List;
