import React from 'react';
import { makeStyles } from '@mui/styles';
import { Typography, Box, Button } from '@mui/material';
import { Link } from 'react-router-dom';
import honeyImage from '../images/honey-comb-pattern.png';

const useStyles = makeStyles(() => ({
  root: {
    backgroundColor: 'white',
    padding: '20px',
  },
  item: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    margin: '20px',
  },
  image: {
    height: '200px',
    width: '200px',
    marginBottom: '10px',
  },
}));

const Basket = ({ cartItems, removeFromCart }) => {
  const classes = useStyles();

  // Group items by type
  const groupedItems = cartItems.reduce((acc, item) => {
    const { type } = item;
    if (!acc[type]) {
      acc[type] = [];
    }
    acc[type].push(item);
    return acc;
  }, {});

  return (
    <Box className={classes.root}>
      {cartItems.length === 0 ? (
        <Typography variant="body1">A kosár üres.</Typography>
      ) : (
        Object.entries(groupedItems).map(([type, items]) => (
          <Box key={type}>
            <Typography variant="h5">{type}</Typography>
            {cartItems.map((item) => (
              <Box className={classes.item} key={item.id}>
                <img className={classes.image} src={honeyImage} alt={item.name} />
                <Typography variant="h6">{item.name}</Typography>
                <Typography variant="body1">{item.description}</Typography>
                <Typography variant="h5">Ár: {item.price} HUF</Typography>
                <Button variant="contained" color="warning" onClick={() => removeFromCart(item)}>
                  Törlés
                </Button>
              </Box>
            ))}
          </Box>
        ))
      )}
      <Link to="/list">
        <Button variant="contained" color="warning">
          Vissza a vásárláshoz
        </Button>
      </Link>
    </Box>
  );
};

export default Basket;
