import React from 'react';
import { makeStyles } from '@mui/styles';
import {
  Typography,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  Button,
  Grid,
  Box,
  Container,
  Divider,
} from '@mui/material';

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  table: {
    minWidth: 650,
  },
  item: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',

    border: `1px solid ${theme.palette.grey[400]}`,
    borderRadius: theme.shape.borderRadius,
  },
  image: {
    maxWidth: 300,
    maxHeight: 300,
  },
  divider: {
    margin: theme.spacing(2, 0),
  },
  button: {
    marginTop: theme.spacing(4),
  },
}));

const BasketPage = () => {
  const classes = useStyles();

  const items = [
    {
      id: 1,
      name: 'Acacia Honey',
      price: 8.99,
      image: 'https://source.unsplash.com/random/300x300/?acacia-honey',
      quantity: 2,
    },
    {
      id: 2,
      name: 'Lavender Honey',
      price: 10.99,
      image: 'https://source.unsplash.com/random/300x300/?lavender-honey',
      quantity: 1,
    },
    {
      id: 3,
      name: 'Wildflower Honey',
      price: 6.99,
      image: 'https://source.unsplash.com/random/300x300/?wildflower-honey',
      quantity: 3,
    },
  ];

  const subtotal = items.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <Container maxWidth="md" className={classes.container}>
      <Typography variant="h4" gutterBottom>
        Shopping Basket
      </Typography>
      <TableContainer component={Paper}>
        <Table className={classes.table}>
          <TableHead>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Total</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {items.map((item) => (
              <TableRow key={item.id}>
                <TableCell>
                  <Box className={classes.item}>
                    <img src={item.image} alt={item.name} className={classes.image} />
                    <Typography variant="h6">{item.name}</Typography>
                  </Box>
                </TableCell>
                <TableCell>{item.price.toFixed(2)} €</TableCell>
                <TableCell>{item.quantity}</TableCell>
                <TableCell>{(item.price * item.quantity).toFixed(2)} €</TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={3} align="right">
                Subtotal
              </TableCell>
              <TableCell>{subtotal.toFixed(2)} €</TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
      <Button variant="contained" color="primary" className={classes.button}>
        Checkout
      </Button>
    </Container>
  );
};

export default BasketPage;
