  return (
    <BrowserRouter>
      <Routes>
          <Route path="/" element={<Login />} />
          <Route path="list" element={<List addToCart={addToCart}/>} />
          <Route path="basket" element={<Basket cartItems={cartItems} removeFromCart={removeFromCart}/>} />
      </Routes>
    </BrowserRouter>
  );
}
