import { Badge, Box, Button, Container, Grid, IconButton, Typography } from '@mui/material';
import { makeStyles } from '@mui/styles';
import { Link } from 'react-router-dom';
import honeyImage  from '../images/honey-comb-pattern.png'

const useStyles = makeStyles(() => ({
    root: {
      backgroundColor: '#f2f2f2',
      padding: '20px',
      minHeight: '100vh',
    },
    item: {
      backgroundColor: '#fff',
      padding: '10px',
      borderRadius: '10px',
      boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.2)',
      transition: 'all 0.3s ease-in-out',
      '&:hover': {
        transform: 'scale(1.05)',
      },
    },
    image: {
      height: '100%',
      width: '100%',
      objectFit: 'cover',
      borderRadius: '10px',
      marginBottom: '10px',
    },
    button: {
      marginTop: '10px',
      backgroundColor: '#f9a826',
      color: '#fff',
      '&:hover': {
        backgroundColor: '#f48c06',
      },
    },
    name: {
      fontWeight: 'bold',
      marginBottom: '5px',
    },
    price: {
      fontWeight: 'bold',
      fontSize: '1.2rem',
    },
    cartButton: {
        position: 'fixed',
        bottom: '20px',
        right: '20px',
        backgroundColor: theme.palette.primary.main,
        color: 'white',
        borderRadius: '50%',
        '&:hover': {
            backgroundColor: theme.palette.primary.dark,
  },
      },
  }));

  const items = [
    {
      id: 1,
      name: 'Akác',
      description: 'Leírás Akác mézről',
      price: 1000
    },
    {
      id: 2,
      name: 'Gyógy',
      description: 'Leírás Gyógy mézről',
      price: 1200
    },
    {
      id: 3,
      name: 'Hárs',
      description: 'Leírás Hárs mézről',
      price: 1500
    },
    {
      id: 4,
      name: 'Virág',
      description: 'Leírás Virág mézről',
      price: 800
    },
    {
      id: 5,
      name: 'Repce',
      description: 'Leírás Repce mézről',
      price: 900
    }
  ];


const List = ({ addToCart }) => {
    const classes = useStyles();

    return (
        <Container maxWidth="lg">
          <Box>
            <Typography variant="h4">Termékek</Typography>
            <Grid container spacing={3}>
              {items.map((type) => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={type.id}>
                  <Box className={classes.item}>
                    <img className={classes.image} src={honeyImage} alt={type.name} />
                    <Typography variant="h5">{type.name}</Typography>
                    <Typography variant="body1" gutterBottom>
                      {type.description}
                    </Typography>
                    <Typography variant="h6" color="primary">
                      {type.price} HUF
                    </Typography>
                    <Button
                      className={classes.button}
                      variant="contained"
                      color="warning"
                      onClick={() => addToCart(type)}
                    >
                      Kosárba teszem
                    </Button>
                  </Box>
                </Grid>
              ))}
            </Grid>
          </Box>
          <Link to="/basket" className={classes.cartButton}>
            <IconButton aria-label="cart">Kosárhoz</IconButton>
          </Link>
        </Container>
      );
};

export default List;
