import React from 'react';
import { makeStyles } from '@mui/styles';
import { Typography, Table, TableHead, TableRow, TableCell, TableBody, Paper } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(4),
    backgroundColor: theme.palette.grey[100],
    minHeight: '100vh',
  },
  title: {
    marginBottom: theme.spacing(2),
  },
  table: {
    marginTop: theme.spacing(2),
  },
}));

const BasketPage: React.FC = () => {
  const classes = useStyles();

  // Get the products from localStorage
  const products = JSON.parse(localStorage.getItem('products') || '[]');

  // Calculate the total price of all products
  const total = products.reduce((acc: number, curr: any) => acc + curr.price * curr.quantity, 0);

  return (
    <div className={classes.root}>
      <Typography variant="h4" component="h1" className={classes.title}>
        Basket
      </Typography>
      {products.length === 0 ? (
        <Typography variant="body1">No products added to basket</Typography>
      ) : (
        <Table className={classes.table}>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Total</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {products.map((product: any) => (
              <TableRow key={product.id}>
                <TableCell>{product.name}</TableCell>
                <TableCell>{product.price.toFixed(2)} €</TableCell>
                <TableCell>{product.quantity}</TableCell>
                <TableCell>{(product.price * product.quantity).toFixed(2)} €</TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={3} align="right">
                Total:
              </TableCell>
              <TableCell>{total.toFixed(2)} €</TableCell>
            </TableRow>
          </TableBody>
        </Table>
      )}
    </div>
  );
};

export default BasketPage;
