import { Box, Button, Container, Grid, Typography } from '@mui/material';
import { makeStyles } from '@mui/styles';
import React from 'react';
import { Link } from 'react-router-dom';
import honeyImage  from '../images/honey-comb-pattern.png'

const useStyles = makeStyles(() => ({
    root: {
      backgroundColor: 'white',
      padding: '20px',
    },
    item: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      margin: '20px',
    },
    image: {
      height: '100%',
      width: '100%',
      marginBottom: '30px',

    },
  }));

  const items = [
    {
      id: 1,
      name: 'Akác',
      description: 'Leírás Akác mézről',
      price: 1000
    },
    {
      id: 2,
      name: 'Gyógy',
      description: 'Leírás Gyógy mézről',
      price: 1200
    },
    {
      id: 3,
      name: 'Hárs',
      description: 'Leírás Hárs mézről',
      price: 1500
    },
    {
      id: 4,
      name: 'Virág',
      description: 'Leírás Virág mézről',
      price: 800
    },
    {
      id: 5,
      name: 'Repce',
      description: 'Leírás Repce mézről',
      price: 900
    }
  ];


const List = ({ addToCart }) => {
  return (
    <Container maxWidth="lg">
      <Box>
        <Typography variant="h4">Termékek</Typography>
        <Grid container>
          {items.map((type) => (
            <Grid item xs={12} sm={6} md={4} lg={3} >
              <Box >
                <img  src={honeyImage}/>
                <Typography variant="h6">{type.name}</Typography>
                <Typography variant="body1">{type.description}</Typography>
                <Typography variant="h5">Ár: {type.price} HUF</Typography>
                <Button variant="contained" color="warning" onClick={() => addToCart(type)}>Add to Cart</Button>
              </Box>
            </Grid>
          ))}
        </Grid>
      </Box>
      <Link to="/basket">
        <Button variant="contained" color="warning">Tovább a kosárhoz</Button>
      </Link>
    </Container>
  );
};

export default List;
