import React, { useState } from 'react';
import { makeStyles } from '@mui/styles';
import { Typography, Grid, Container, Box, Button } from '@mui/material';

const honeyTypes = ['Akác', 'Gyógy', 'Hárs', 'Virág', 'Repce'];

const useStyles = makeStyles(() => ({
  root: {
    backgroundColor: 'white',
    padding: '20px',
  },
  item: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    margin: '20px',
  },
  image: {
    height: '200px',
    width: '200px',
    marginBottom: '10px',
  },
}));

const List: React.FC = () => {
  const classes = useStyles();
  const [cartItems, setCartItems] = useState<string[]>([]);

  const addToCart = (item: string) => {
    
    setCartItems([...cartItems, item]);
  };

  return (
    <Container maxWidth="lg">
      <Box className={classes.root}>
        <Typography variant="h4">Termékek</Typography>
        <Grid container>
          {honeyTypes.map((type) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={type}>
              <Box className={classes.item}>
                <img className={classes.image} src="https://via.placeholder.com/200" alt={type} />
                <Typography variant="h6">{type}</Typography>
                <Typography variant="body1">Leírás</Typography>
                <Typography variant="h5">Ár: 1000 HUF</Typography>
                <Button variant="contained" color="warning" onClick={() => addToCart(type)}>
                  Kosárba
                </Button>
              </Box>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default List;
