import React from 'react';
import { makeStyles } from '@mui/styles';
import { Typography, Box, Button } from '@mui/material';
import { Link } from 'react-router-dom';
import honeyImage from '../images/honey-comb-pattern.png'

const useStyles = makeStyles(() => ({
  root: {
    backgroundColor: 'white',
    padding: '20px',
  },
  item: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    margin: '20px',
  },
  image: {
    height: '200px',
    width: '200px',
    marginBottom: '10px',
  },
  deleteButton: {
    marginTop: '10px',
  },
}));

const Basket = ({ cartItems, setCartItems }) => {
  const classes = useStyles();

  const handleDeleteItem = (id) => {
    const newCartItems = cartItems.filter(item => item.id !== id);
    setCartItems(newCartItems);
  };

  return (
    <Box className={classes.root}>
      <Typography variant="h4">Kosár</Typography>
      {cartItems.length === 0 ? (
        <Typography variant="body1">A kosár üres.</Typography>
      ) : (
        cartItems.map((item, index) => (
          <Box className={classes.item} key={index}>
            <img className={classes.image} src={honeyImage} alt={item.name} />
            <Typography variant="h6">{item.name}</Typography>
            <Typography variant="body1">{item.description}</Typography>
            <Typography variant="h5">Ár: {item.price} HUF</Typography>
            <Button
              variant="contained"
              color="error"
              className={classes.deleteButton}
              onClick={() => handleDeleteItem(item.id)}
            >
              Törlés
            </Button>
          </Box>
        ))
      )}
      <Link to="/list">
        <Button variant="contained" color="warning">Vissza a vásárláshoz</Button>
      </Link>
    </Box>
  );
};

export default Basket;
