import React from 'react';
import { makeStyles } from '@mui/styles';
import {
  Typography,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  Button,
  Grid,
  Box,
  Container,
  Divider,
} from '@mui/material';
import { useLocation } from 'react-router-dom';
<Basket cartItems={cartItems} />

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  table: {
    minWidth: 650,
  },
  item: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  image: {
    maxWidth: 300,
    maxHeight: 300,
  },
  divider: {

  },
  button: {

  },
}));

const BasketPage = () => {
  const classes = useStyles();
  const location = useLocation();

  const items = location.state.cartItems;

  const subtotal = items.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <Container maxWidth="md" className={classes.container}>
      <Typography variant="h4" gutterBottom>
      Kosár
      </Typography>
      <TableContainer component={Paper}>
        <Table className={classes.table}>
          <TableHead>
            <TableRow>
              <TableCell>Termék</TableCell>
              <TableCell>Ár</TableCell>
              <TableCell>Mennyiség</TableCell>
              <TableCell>Összeg</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {items.map((item) => (
              <TableRow key={item.id}>
                <TableCell>
                  <Box className={classes.item}>
                    <img src={item.image} alt={item.name} className={classes.image} />
                    <Typography variant="h6">{item.name}</Typography>
                  </Box>
                </TableCell>
                <TableCell>{item.price.toFixed(2)} HUF</TableCell>
                <TableCell>{item.quantity}</TableCell>
                <TableCell>{(item.price * item.quantity).toFixed(2)} HUF</TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={3} align="right">
                Teljes összeg
              </TableCell>
              <TableCell>{subtotal.toFixed(2)} HUF</TableCell>
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
      <Button variant="contained" color="primary" className={classes.button}>
      Megrendelés
      </Button>
    </Container>
  );
};

export default BasketPage;
